            // Бесконечный цикл перезаргузки карты
            while (true)
            {
                // Установка рюгзака по x и y
                Console.SetCursorPosition(0, 28);

                Console.Write("Рюкзак: ");

                // for для рюкзака
                for (int i = 0 ; i < bag.Length; i++)
                {
                    Console.Write(bag[i] + " ");
                }

                // Установка карты по x и y
                Console.SetCursorPosition(0, 0);

